import { getConnection } from "./../database/database"

const getUsers = async (req, res) =>{
    try{
    const connection = await getConnection();
    const result = await connection.query("SELECT * FROM usuarios");
    res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }   
};

const addUser = async (req, res) =>{
    try{
    const { correo, usuario, contrasea , type } = req.body;

    if(correo == undefined || usuario == undefined || contrasea == undefined || type == undefined){
        res.status(400).json({message : "Bad request, please fill all the inputs"})
    }

    const connection = await getConnection(); 
    const result = await connection.query(`INSERT INTO usuarios (correo, usuario, contrasea, type)
    VALUES ("${correo}", "${usuario}", "${contrasea}", ${type});`);
    res.json({message: "User added"})
    }catch(error){
        res.status(500);
        res.send(error.message);
    }   
};

const getOneUser = async (req, res) =>{
    try{
    const { id }=req.params;
    const connection = await getConnection();
    const result = await connection.query("SELECT * FROM usuarios WHERE id = ?", id);
    res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }   
};

const UpdateUser = async (req, res) =>{
    try{
    const { id }=req.params;
    const { correo, usuario, contrasea , type } = req.body;
    
    if(id == undefined ||correo == undefined || usuario == undefined || contrasea == undefined || type == undefined){
        res.status(400).json({message : "Bad request, please fill all the inputs"})
    }

    const connection = await getConnection();
    const result = await connection.query(`UPDATE usuarios SET
    correo="${correo}", usuario="${usuario}", contrasea="${contrasea}", type="${type}" WHERE id= ${id};`);
    res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }   
};

const deleteUser = async (req, res) =>{
    try{
    const { id }=req.params;
    const connection = await getConnection();
    const result = await connection.query("DELETE FROM usuarios WHERE id = ?", id);
    res.json(result);
    }catch(error){
        res.status(500);
        res.send(error.message);
    }   
};

export const methods = {
    getUsers,
    addUser,
    getOneUser,
    deleteUser,
    UpdateUser
};

